using System;

public class Program
{
	public static void Main()
	{
		string input =
		"";

		// Pt. 1
		unique(input, 4);

		// Pt. 2
		unique(input, 14);
	}

	private static void unique(string str, int uniqueInARow) {
		for (int counter = 0; counter <= str.Length - uniqueInARow; counter++)
		{
			string sub = str.Substring(counter, uniqueInARow);
			if (allUnique(sub)) {
				Console.WriteLine("String {0} unique from index {1} to {2}", sub, counter, counter + uniqueInARow);
				return;
			}
		}
	}

	private static bool allUnique(string str) {
		bool[] array = new bool[256];

		foreach (char value in str)
		  if (array[(int) value])
			return false;
		  else
			array[(int)value] = true;

		return true;
	}
}