fun main() {
    val input = ""

    // Pt.1
    unique(input, 4)

    // Pt.2
    unique(input, 14)
}

fun unique(input: String, uniqueInARow: Int) {
    for (i in 0..input.length - uniqueInARow) {
        val sub = input.substring(i, i + uniqueInARow)
        if (allUnique(sub)) {
            println("String $sub unique from index $i to ${i + uniqueInARow}")
            return
        }
    }
}

fun allUnique(input: String) : Boolean {
    val array = BooleanArray(256)

    for (char in input) {
        if (array[char.code])
            return false;
        else
            array[char.code] = true;
    }
    return true;
}